mapper.select:
SELECT *
  FROM Member
 WHERE MemberID = #{MemberID}
   AND MemberPW = #{MemberPW}

mapper.update:
UPDATE Member 
SET MemberPW = #{MemberPW}, MemberEmail = #{MemberEmail}, MemberAddress = #{MemberAddress}, MemberPW = #{MemberPW}
WHERE MemberID = #{MemberID}

mapper.select:
SELECT *
FROM Member
WHERE MemberID = #{MemberID} and MemberEmail = #{MemberEmail}

mapper.update:
UPDATE Member
SET MemberPW = #{MemberPW}
WHERE MemberID = #{MemberID} and MemberEmail = #{MemberEmail}
